BOOLEAN DeskMouseOverlapCheck(WINDOW *pWindow){
    GRAPHIC_POSITION Position;
    
    Position.X=DeskMouseCtrl.MousePointer.X;
    Position.Y=DeskMouseCtrl.MousePointer.Y;
    if(DesklibAreaPositionCheck(&pWindow->DisplayArea,&Position)==TRUE) return TRUE;
    Position.X=DeskMouseCtrl.MousePointer.X+DESK_MOUSE_MAP_WIDTH;
    Position.Y=DeskMouseCtrl.MousePointer.Y;
    if(DesklibAreaPositionCheck(&pWindow->DisplayArea,&Position)==TRUE) return TRUE;
    Position.X=DeskMouseCtrl.MousePointer.X;
    Position.Y=DeskMouseCtrl.MousePointer.Y+DESK_MOUSE_MAP_HEIGHT;
    if(DesklibAreaPositionCheck(&pWindow->DisplayArea,&Position)==TRUE) return TRUE;
    Position.X=DeskMouseCtrl.MousePointer.X+DESK_MOUSE_MAP_WIDTH;
    Position.Y=DeskMouseCtrl.MousePointer.Y+DESK_MOUSE_MAP_HEIGHT;
    if(DesklibAreaPositionCheck(&pWindow->DisplayArea,&Position)==TRUE) return TRUE;
    return FALSE;    
}